}
cobs\_fifo\_status\_e cobs\_fifo\_push(cobs\_fifo\_t\* fifo, uint8\_t data)
{
 MRT\_MUTEX\_LOCK(fifo->mMutex);

 // next is where head will point to after this write.
 int next = fifo->mHead + 1;

 if (next > fifo->mMaxLen)
 {
 next = 0;
 }
 if (next == fifo->mTail) // check if circular buffer is full
 {
 MRT\_MUTEX\_UNLOCK(fifo->mMutex);
 return COBS\_FIFO\_OVERFLOW;
 }

 fifo->mCount++;

 //With byte Stuffing the only zero bytes are frame boundaries
 if(data == 0 )
 {
 fifo->mFrameCount++;

 //if next frame is not set yet
 if(fifo->mNextLen == 0)
 {
 fifo->mNextLen = fifo->mCount;
 }
 }

 fifo->mBuffer[fifo->mHead] = data;

 fifo->mHead = next; // head to next data offset.

 MRT\_MUTEX\_UNLOCK(fifo->mMutex);

 return COBS\_FIFO\_OK; // return success to indicate successful push.
}
cobs\_fifo\_status\_e cobs\_fifo\_pop(cobs\_fifo\_t\* fifo, uint8\_t\* data)
{
 MRT\_MUTEX\_LOCK(fifo->mMutex);
 // if the head isn't ahead of the tail, we don't have any characters
 if (fifo->mHead == fifo->mTail) // check if circular buffer is empty
 {
 MRT\_MUTEX\_UNLOCK(fifo->mMutex);
 return COBS\_FIFO\_UNDERFLOW; // and return with an error
 }

 // next is where tail will point to after this read.
 int next = fifo->mTail + 1;
 if (next > fifo->mMaxLen)
 {
 next = 0;
 }

 \*data = fifo->mBuffer[fifo->mTail];

 if(\*data == 0)
 {
 fifo->mFrameCount--;
 if(fifo->mNextLen >=0)
 fifo->mNextLen--;
 }

 fifo->mTail = next; // tail to next data offset.

 if(fifo->mCount > 0)
 {
 fifo->mCount--;
 }
 MRT\_MUTEX\_UNLOCK(fifo->mMutex);

 return COBS\_FIFO\_OK; // return success to indicate successful push.
}
