def get_children_ids (self):
return [i for i in self.children.keys()]
#ok

def get_children(self):
return self.children.values()

def get_parents(self):
return self.parents.values()

def set_id(self, i):
self.id = i
#ok

def set_label(self, i):
self.label = i
#ok

def set_parents_ids(self, i):
self.parents = i
#ok

def set_children_ids(self, i):
self.children = i
#ok

def add_child_id(self, i):
if i in self.get_children_ids():
self.children[i] +=1
else:
self.children[i] = 1
#ok

def add_parent_id(self, i):
if i in self.get_parents_ids():
self.parents[i] +=1
else:
self.parents[i] = 1
#ok

def remove_parent_once(self, id):
try:
if self.parents[id] <= 1:
self.remove_parent_id(id)
return
self.parents[id] = self.parents[id] - 1
except KeyError:
raise “Invalid parent id”
#ok

def remove_child_once(self, id):
try:
if self.children[id] <= 1:
self.remove_child_id(id)
return
self.children[id] = self.children[id] - 1
except KeyError:
raise “Invalid child id”
#ok

def remove_parent_id(self, id):
try:
self.parents.pop(id)
except KeyError:
raise “Invalid parent id”
#ok

def remove_child_id(self, id):
try:
self.children.pop(id)
except KeyError:
raise “Invalid child id”
#ok

def indegree(self):
res = 0
for p in self.parents.values():
res += p
return res

def outdegree(self):
res = 0
for c in self.children.values():
res +=c
return res

def degree(self):
return self.indegree()+self.outdegree()

