d.outputs = self.outputs.copy()
for key in self.nodes.keys():
n0 = node(-1, ‘’, {}, {})
n0.id = self.nodes[key].id
n0.label = self.nodes[key].label
n0.parents = self.nodes[key].parents.copy()
n0.children = self.nodes[key].children.copy()
newNodes[key]=n0
d.nodes = newNodes
