def add_node(self, label=‘’, parents=None, children=None): #parents et children sont des dictionnaires
‘’‘
adds a NEW (it creates it !) a node
label : string
parents : int:int dict
children : int:int dict
‘’’
if parents is None:
parents = {}
if children is None:
children = {}
new_id = self.new_id()
new_node = node(new_id, label, parents, children)
