using UnityEngine.SceneManagement;
using UnityEngine.UI;
using UnityEngine;

public class GameManager : MonoBehaviour {

    public static GameManager instance;
    public GameObject faderObj;
    public Image faderImg;
    public bool gameOver;
    public float fadeSpeed, fadeTransparency;
    public string currentScene;
    public AsyncOperation async;

    private void Awake() {
        if (instance != null) {
            Destroy(gameObject);
        } else {
            instance = this;
            DontDestroyOnLoad(gameObject);
        }
    }

    private void Update() {
        if (Input.GetKeyDown(KeyCode.Escape)) {
            ExitGame();
        }
    }

    public void LoadScene(string sceneName) {
        currentScene = sceneName;
        async = SceneManager.LoadSceneAsync(sceneName);
        async.allowSceneActivation = false;
        FadeOut(faderObj, faderImg);
    }

    public void ReloadScene() {
        LoadScene(currentScene);
    }

    private void OnLevelFinishedLoading(Scene scene, LoadSceneMode mode) {
        ActivateScene();
        FadeIn(faderObj, faderImg);
    }

    public void FadeOut(GameObject faderObject, Image fader) {
        StartCoroutine(FadeOutCoroutine(faderObject, fader));
    }

    public void FadeIn(GameObject faderObject, Image fader) {
        StartCoroutine(FadeInCoroutine(faderObject, fader));
    }

    private IEnumerator FadeOutCoroutine(GameObject faderObject, Image fader) {
        faderObject.SetActive(true);
        Color tempColor = fader.color;
        while (tempColor.a < fadeTransparency) {
            tempColor.a += Time.deltaTime / fadeSpeed;
            fader.color = tempColor;
            yield return null;
        }
        async.allowSceneActivation = true;
    }

    private IEnumerator FadeInCoroutine(GameObject faderObject, Image fader) {
        Color tempColor = fader.color;
        while (tempColor.a > 0.0f) {
            tempColor.a -= Time.deltaTime / fadeSpeed;
            fader.color = tempColor;
            yield return null;
        }
        faderObject.SetActive(false);
    }

    public void ActivateScene() {
        GameObject[] objects = SceneManager.GetActiveScene().GetRootGameObjects();
        foreach (GameObject obj in objects) {
            obj.SetActive(true);
        }
    }

    public string CurrentSceneName {
        get { return currentScene; }
    }

    public void ExitGame() {
        Application.Quit();
    }

    public void ReturnToMenu() {
        LoadScene("MainMenu");
    }
}

