function determineWinners(players, sharedCards) {
    const activePlayers = players.filter((player) => player.inHand);
    const playerEvaluations = activePlayers.map((player) => evaluateHand(player.hand.concat(sharedCards)));

    let maxRank = null;
    const winners = [];
    let maxTiebreakers = [];

    for (const { rank, tiebreaker } of playerEvaluations) {
        if (maxRank === null || rank > maxRank) {
            maxRank = rank;
            maxTiebreakers = [tiebreaker];
        } else if (rank === maxRank) {
            maxTiebreakers.push(tiebreaker);
        }
    }

    for (let i = 0; i < activePlayers.length; i++) {
        const { rank, tiebreaker } = playerEvaluations[i];

        if (rank !== maxRank) {
            continue;
        }

        if (maxTiebreakers.every((maxTiebreaker) => compareTiebreakers(tiebreaker, maxTiebreaker))) {
            winners.push(players.indexOf(activePlayers[i]));
        }
    }

    return winners;
}

